mapper.update:
UPDATE JOINMEMBER SET member_password = #{member_password}
	where member_id=#{member_id} 
		and member_name=#{member_name}
			and password_a=#{password_a}

mapper.select:
select user1,user2,FRIEND_TYPE,FRIEND_STATUS from friends
where user1=#{user1}
and friend_type=0

mapper.update:
UPDATE FRIENDS SET FRIEND_TYPE = 1,FRIEND_STATUS=0 
	where (user1=#{user1} 
	 AND  user2=#{user2})
	 OR (user2=#{user1} 
	 AND user1=#{user2})

mapper.delete:
delete from FRIENDS
where  user1=#{user1} 
and	user2=#{user2}

delete from FRIENDS
where  user2=#{user2} 
or	user1=#{user2}
